//Para Ie cambia el estilo del info maouse
function ieFix(){
	//Objeto en mscross.js
	var browser = new Browser();
	
	if (browser.isIE)	document.getElementById('info_mouse').className = 'info_mouse_ie';
}

function init(op_depto,id_depto_filtro){
	
	//Fix para cuando uno consulta los mpios de un depto y quiere volver a ver el mapa de depto
	if (id_depto_filtro == 0){
		document.getElementById(id_hidden).value = extent_org;
		
		//oculta combo de mpio
		document.getElementById('comboBoxMunicipio').innerHTML = '';
	}
	
	//Para el mousemove
	mouseOutMapa();
	
	//Funcion en /js/mapserver.js
	var map_extent = parseMapExtent(id_hidden);
	var control;
	
	if (id_depto_filtro == 0)	control = 'consulta_depto';
	else						control = 'consulta_mpio';
	
	document.getElementById('op_depto').value = op_depto;
		
	//Vacia el div del mapa por si tiene alguno
	document.getElementById('map_tag').innerHTML = '';
	
	//oculta div ini
	document.getElementById('ini').style.display = 'none';
	
	//Pasa la variable como string, para que mantenga el 0 en casos como id_depto = 05
	id_depto = new String(id_depto_filtro);
		
	myMap = new msMap(document.getElementById('map_tag'),control);
	myMap.setCgi(server + 'sissh/consulta/mapserver_consulta.php');
	myMap.setArgs('case=consulta&id_depto_filtro='+id_depto+'&op_depto='+op_depto);
	myMap.setFullExtent(map_extent[0],map_extent[1],map_extent[2],map_extent[3]); // (xmin,xmax,ymin,ymax)

	myMap.redraw();
	myMap.debug();
	
	return false;
}

function loadMapAreaMpios(id_depto,nombre){
	
	document.getElementById('map_mpios').innerHTML = '';

	//Cache
	getDataV1('checkCacheMapArea','ajax_data.php?object=checkCacheMapArea&id_depto='+id_depto,'map_mpios');
}

function genMapArea(){

	var input = document.getElementById('map_mpios');
	var width = parseInt(document.getElementById('map_tag').style.width);
	var height = parseInt(document.getElementById('map_tag').style.height);
	var id_depto = document.getElementById('id_depto').value;

	if (input.innerHTML == ''){
		//Modifica el .map, con el depto que es
		getDataV1('loadMapAreaMpios','ajax_data.php?object=setMapFile&id_depto='+id_depto+'&width='+width+'&height='+height,'map_mpios');
		//setTimeout("genMapAreaExec('"+id_depto+"')",200);
	}
}

function genMapAreaExec(){
	var id_depto = document.getElementById('id_depto').value;
	
	getDataV1('',server+'cgi-bin/mapserv?map='+document_root+'sissh/consulta/test_mapserver/test_map_area_mpio.map&qlayer=mpios&mode=nquery&searchmap=true','map_mpios');
	getDataExec('ajax_data.php?object=saveCacheMapArea&id_depto='+id_depto);
}

function mouseOverMapa(text){

	var div = document.getElementById('info_mouse');
	
	div.style.visibility = 'visible';

	div.innerHTML = '<p>' + text + '</p>';
	document.onmousemove = followmouse;
}

function mouseOutMapa(){

	var div = document.getElementById('info_mouse');
	div.style.visibility = 'hidden';
	document.onmousemove = null;
}

function clickMapa(id,nombre){
	var op_depto = parseInt(document.getElementById('op_depto').value);
	
	if (op_depto == 1){
		//funcion en consulta/minificha.php
		asignarVariablesH(id,'');
		
		//funcion en /js/mapserver.js
		setExtentByDepto(id,"map_extent");
		
		setTimeout("init(1,'"+id+"')",500);
		
		unHideOptions();
	}
	else{
		//input en consulta/minificha.php
		document.getElementById('id_depto').value = id;
		
		loadMapAreaMpios(id,nombre);
		setExtentByDepto(id,"map_extent");
		setTimeout("init(0,'"+id+"')",500);
		setTimeout("listarMunicipios('"+id+"')",500);
	}
}

function clickMapaMpio(id){
	//funcion en consulta/minificha.php
	asignarVariablesH(document.getElementById('id_depto').value,id);
	
	unHideOptions();
}

function listarMunicipios(id_depto){

	var id_deptos = id_depto;

	getDataV1('comboBoxMunicipio','admin/ajax_data.php?object=comboBoxMunicipio&id_name=id_mun_depto&multiple=0&separador_depto=0&titulo=0&id_deptos='+id_deptos,'comboBoxMunicipio')
	
	//Agrega el evento onChange
	setTimeout("setOnChangeComboMpio()",1000);
}

function setOnChangeComboMpio(){

	var combo = document.getElementById('id_mun_depto');

	if (combo != undefined)	
		combo.onchange = function() { clickMapaMpio(combo.value) };
	else
		//Agrega el evento onChange
		setTimeout("setOnChangeComboMpio()",1000);
		
}

function unHideOptions(){
	location.href = '#ubi_rta';
	
	if (document.getElementById('btns_gen') != undefined){
		document.getElementById('btns_gen').style.display = '';
	}
}
